  const option = 'create'
  const handleChange = () => {
    console.log('changed')
  }
  return (
    <div className="overlay">
      <div className="modal">
        <div className="header">
          <div className="title">Let's {option} your task</div>
          <div className="close">X</div>
        </div>
        <div className="body">
          <form>
            <input
              required
              maxLength="30"
              placeholder="Task title"
              name="title"
              value={""}
              onChange={handleChange}
            />
            <input
              required
              type="range"
              min="0"
              max="10"
              name="progress"
              value={""}
              onChange={handleChange}
            />
            <input />
            <input className={option} type="submit" />
          </form>
        </div>
      </div>
    </div>
  );
}

export default Modal;
